CREATE DATABASE FinancialOptionsSystem;
GO

USE FinancialOptionsSystem;
GO

-- Таблица Active (Актив)
CREATE TABLE Active
(
    Active_ID BIGINT PRIMARY KEY,
    Region INT NOT NULL,
    Price DECIMAL(18,4) NOT NULL,
    Volume_of_dividends DECIMAL(18,4) NOT NULL,
    Calendar_frequency INT NOT NULL,
    Profitability_of_dividends DECIMAL(10,4) NOT NULL,
    Type_of_active CHAR(15) NOT NULL,
    Active_style CHAR(15) NOT NULL,
    Active_currency VARCHAR(25) NOT NULL,
	RiskFreeRate DECIMAL(10,6) NOT NULL,
    
    -- Проверочные ограничения
    CONSTRAINT CHK_Region CHECK (Region > 0),
    CONSTRAINT CHK_Price CHECK (Price >= 0),
    CONSTRAINT CHK_Volume_of_dividends CHECK (Volume_of_dividends >= 0),
    CONSTRAINT CHK_Active_style CHECK (Active_style IN ('american', 'european')),
    CONSTRAINT CHK_Active_currency CHECK (LEN(Active_currency) >= 3)
);
GO

-- Таблица Dividend (Дивиденд)
CREATE TABLE Dividend
(
    Dividend_ID BIGINT IDENTITY(1,1) PRIMARY KEY,
    Date DATE NOT NULL,
    Dividend_amount DECIMAL(18,4) NOT NULL,
    Active_ID BIGINT NOT NULL,
    
    -- Внешний ключ на таблицу Active
    CONSTRAINT FK_Dividend_Active FOREIGN KEY (Active_ID) 
        REFERENCES Active(Active_ID) ON DELETE CASCADE,
    
    -- Проверочные ограничения
    CONSTRAINT CHK_Dividend_amount CHECK (Dividend_amount >= 0)
);
GO

-- Таблица Quote (Котировка)
CREATE TABLE Quote
(
    Quote_ID BIGINT IDENTITY(1,1) PRIMARY KEY,
    Currency VARCHAR(25) NOT NULL,
    Day INT NOT NULL,
    Quote DECIMAL(18,4) NOT NULL,
    Active_ID BIGINT NOT NULL,
    
    -- Внешний ключ на таблицу Active
    CONSTRAINT FK_Quote_Active FOREIGN KEY (Active_ID) 
        REFERENCES Active(Active_ID) ON DELETE CASCADE,
    
    -- Проверочные ограничения
    CONSTRAINT CHK_Quote_Currency CHECK (LEN(Currency) >= 3),
    CONSTRAINT CHK_Quote CHECK (Quote > 0)
);
GO

-- Таблица Option (Опцион)
CREATE TABLE [Option]
(
    Option_ID BIGINT PRIMARY KEY,
    Option_Call_ID BIGINT NOT NULL,
    Option_Put_ID BIGINT NOT NULL,
    Expiration_date DATE NOT NULL,
    Strike DECIMAL(18,4) NOT NULL,
    Option_call_price DECIMAL(18,4) NOT NULL,
    Option_put_price DECIMAL(18,4) NOT NULL,
    ATM_position BIGINT NOT NULL,
    Predefined_IV_for_call DECIMAL(10,6) NOT NULL,
    Predefined_IV_for_put DECIMAL(10,6) NOT NULL,
    Expected_dividend_yield DECIMAL(10,4) NOT NULL,
    Estimated_asset_value DECIMAL(18,4) NOT NULL,
    Call_Bid DECIMAL(18,4) NOT NULL,
    Call_Ask DECIMAL(18,4) NOT NULL,
    Put_Bid DECIMAL(18,4) NOT NULL,
    Put_Ask DECIMAL(18,4) NOT NULL,
    Quarterly_option_flag INT NOT NULL,
    Active_ID BIGINT NOT NULL,
    
    -- Внешний ключ на таблицу Active
    CONSTRAINT FK_Option_Active FOREIGN KEY (Active_ID) 
        REFERENCES Active(Active_ID) ON DELETE CASCADE,
    
    -- Проверочные ограничения
    CONSTRAINT CHK_Strike CHECK (Strike > 0),
    CONSTRAINT CHK_Option_call_price CHECK (Option_call_price >= 0),
    CONSTRAINT CHK_Option_put_price CHECK (Option_put_price >= 0),
    CONSTRAINT CHK_ATM_position CHECK (ATM_position IN (-1, 0, 1)),
    CONSTRAINT CHK_Predefined_IV_for_call CHECK (Predefined_IV_for_call >= 0 AND Predefined_IV_for_call <= 5),
    CONSTRAINT CHK_Predefined_IV_for_put CHECK (Predefined_IV_for_put >= 0 AND Predefined_IV_for_put <= 5),
    CONSTRAINT CHK_Expected_dividend_yield CHECK (Expected_dividend_yield >= 0 AND Expected_dividend_yield <= 100),
    CONSTRAINT CHK_Estimated_asset_value CHECK (Estimated_asset_value > 0),
    CONSTRAINT CHK_Call_Bid CHECK (Call_Bid >= 0),
    CONSTRAINT CHK_Call_Ask CHECK (Call_Ask >= 0 AND Call_Ask >= Call_Bid),
    CONSTRAINT CHK_Put_Bid CHECK (Put_Bid >= 0),
    CONSTRAINT CHK_Put_Ask CHECK (Put_Ask >= 0 AND Put_Ask >= Put_Bid),
    CONSTRAINT CHK_Quarterly_option_flag CHECK (Quarterly_option_flag IN (0, 1))
);
GO

-- Таблица Output (Вывод)
CREATE TABLE Output
(
    Output_ID BIGINT IDENTITY(1,1) PRIMARY KEY,
    Date DATE NOT NULL,
    Region INT NOT NULL,
    
    -- Проверочные ограничения
    CONSTRAINT CHK_Output_Region CHECK (Region > 0)
);
GO

-- Создание индексов для улучшения производительности

-- Индексы для таблицы Active
CREATE INDEX IX_Active_Region ON Active(Region);
CREATE INDEX IX_Active_Type ON Active(Type_of_active);
CREATE INDEX IX_Active_Currency ON Active(Active_currency);

-- Индексы для таблицы Dividend
CREATE INDEX IX_Dividend_ActiveID ON Dividend(Active_ID);
CREATE INDEX IX_Dividend_Date ON Dividend(Date);
CREATE INDEX IX_Dividend_ActiveID_Date ON Dividend(Active_ID, Date);

-- Индексы для таблицы Quote
CREATE INDEX IX_Quote_ActiveID ON Quote(Active_ID);
CREATE INDEX IX_Quote_Day ON Quote(Day);
CREATE INDEX IX_Quote_ActiveID_Day ON Quote(Active_ID, Day);

-- Индексы для таблицы Option
CREATE INDEX IX_Option_ActiveID ON [Option](Active_ID);
CREATE INDEX IX_Option_ExpirationDate ON [Option](Expiration_date);
CREATE INDEX IX_Option_Strike ON [Option](Strike);
CREATE INDEX IX_Option_ActiveID_Expiration ON [Option](Active_ID, Expiration_date);
CREATE INDEX IX_Option_CallID ON [Option](Option_Call_ID);
CREATE INDEX IX_Option_PutID ON [Option](Option_Put_ID);

-- Индексы для таблицы Output
CREATE INDEX IX_Output_Date ON Output(Date);
CREATE INDEX IX_Output_Region ON Output(Region);
CREATE INDEX IX_Output_Date_Region ON Output(Date, Region);
